CREATE VIEW [dbo].[EXCHANGERATEDENTODENAFTERBOTHSTART] AS SELECT T1.CURRENCYCODE AS CURRENCYCODE1,T1.STARTDATE AS STARTDATE1,T1.EXCHANGERATE AS EXCHANGERATE1,T1.PARTITION AS PARTITION,T1.RECID AS RECID,T2.PARTITION AS PARTITION#2,T2.STARTDATE AS STARTDATE2,T2.EXCHANGERATE AS EXCHANGERATE2,T2.CURRENCYCODE AS CURRENCYCODE2,T3.PARTITION AS PARTITION#3,T3.RECID AS EXCHANGERATETYPE FROM  EXCHANGERATEEURODENOMINATIONVIEW T1 LEFT OUTER JOIN CURRENCYEURODENOMINATION T2 ON (T2.CURRENCYCODE<>T1.CURRENCYCODE AND (T2.PARTITION = T1.PARTITION)) LEFT OUTER JOIN EXCHANGERATETYPE T3 ON (T1.PARTITION=T3.PARTITION)
