CREATE VIEW [dbo].[EXCHANGERATEDENTODENBETWEENSTART] AS SELECT T1.CURRENCYCODE AS CURRENCYCODE1,T1.STARTDATE AS STARTDATE1,T1.EXCHANGERATE AS EURODENOMINATIONRATE1,T1.EUROCURRENCYCODE AS EUROCURRENCYCODE,T1.PARTITION AS PARTITION,T1.RECID AS RECID,T2.PARTITION AS PARTITION#2,T2.CURRENCYCODE AS CURRENCYCODE2,T2.STARTDATE AS STARTDATE2,T2.EXCHANGERATE AS EURODENOMINATIONRATE2,T3.PARTITION AS PARTITION#3,T3.EXCHANGERATETYPE AS EXCHANGERATETYPE,T3.FROMCURRENCYCODE AS FROMCURRENCYCODE,T3.TOCURRENCYCODE AS TOCURRENCYCODE,T4.PARTITION AS PARTITION#4,T4.EXCHANGERATE AS EXCHANGERATE,T4.VALIDFROM AS VALIDFROM,T4.VALIDTO AS VALIDTO FROM  EXCHANGERATEEURODENOMINATIONVIEW T1 LEFT OUTER JOIN CURRENCYEURODENOMINATION T2 ON (T1.PARTITION=T2.PARTITION) CROSS JOIN EXCHANGERATECURRENCYPAIR T3 CROSS JOIN EXCHANGERATE T4 WHERE (T3.RECID=T4.EXCHANGERATECURRENCYPAIR AND (T3.PARTITION = T4.PARTITION))
