CREATE VIEW [dbo].[EXCHANGERATEDENTOEUROAFTERSTART] AS SELECT T1.EUROCURRENCYCODE AS EUROCURRENCYCODE,T1.CURRENCYCODE AS CURRENCYCODE,T1.STARTDATE AS STARTDATE,T1.EXCHANGERATE AS EXCHANGERATE,T1.PARTITION AS PARTITION,T1.RECID AS RECID,T2.PARTITION AS PARTITION#2,T2.RECID AS EXCHANGERATETYPE FROM  EXCHANGERATEEURODENOMINATIONVIEW T1 LEFT OUTER JOIN EXCHANGERATETYPE T2 ON (T1.PARTITION=T2.PARTITION)
