CREATE VIEW [dbo].[EXCHANGERATEEURODENOMINATIONVIEW] AS SELECT T1.CURRENCYCODE AS CURRENCYCODE,T1.EXCHANGERATE AS EXCHANGERATE,T1.STARTDATE AS STARTDATE,T1.PARTITION AS PARTITION,T1.RECID AS RECID,T2.PARTITION AS PARTITION#2,T2.CURRENCYCODE AS EUROCURRENCYCODE FROM  CURRENCYEURODENOMINATION T1 LEFT OUTER JOIN CURRENCY T2 ON ((T2.ISEURO=1) AND (T1.PARTITION=T2.PARTITION))
