CREATE VIEW [dbo].[EXCHANGERATEFIXEDCURRENCYVIEW] AS SELECT T1.CURRENCYCODE AS CURRENCYCODE,T1.EXCHANGERATE AS EXCHANGERATE,T1.STARTDATE AS STARTDATE,T1.RECID AS RECID1,T1.PARTITION AS PARTITION,T1.RECID AS RECID,(CAST (((SELECT top 1 Currency.CurrencyCode FROM Currency WHERE Currency.IsEuro = 1)) AS NVARCHAR(10))) AS EUROCURRENCYCODE FROM CURRENCYEURODENOMINATION T1
