CREATE VIEW [dbo].[EXCHANGERATESAMEFROMTOCURRENCYVIEW] AS SELECT T1.RECID AS EXCHANGERATETYPERECID,T1.PARTITION AS PARTITION,T1.RECID AS RECID,T2.PARTITION AS PARTITION#2,T2.CURRENCYCODE AS FROMCURRENCYCODE,T2.CURRENCYCODE AS TOCURRENCYCODE,(CAST (('19000101') AS DATETIME)) AS VALIDFROM,(CAST (('21541231') AS DATETIME)) AS VALIDTO,(CAST ((100.0) AS NUMERIC(32,16))) AS EXCHANGERATE FROM  EXCHANGERATETYPE T1 LEFT OUTER JOIN CURRENCY T2 ON (T1.PARTITION=T2.PARTITION)
