
CREATE VIEW [dbo].[INVENTITEMPRICECURRENTACTIVE] AS SELECT MAX(T1.CREATEDDATETIME) AS MAXCREATEDDATETIME,MAX(T1.ACTIVATIONDATE) AS MAXACTIVATIONDATE,T1.ITEMID AS ITEMID,T1.VERSIONID AS VERSIONID,T1.PRICETYPE AS PRICETYPE,T1.INVENTDIMID AS INVENTDIMID,T1.DATAAREAID AS DATAAREAID,T1.PARTITION AS PARTITION,1010 AS RECID FROM INVENTITEMPRICE T1 GROUP BY T1.ITEMID,T1.VERSIONID,T1.PRICETYPE,T1.INVENTDIMID,T1.DATAAREAID,T1.PARTITION
